def selection(arr):
    for i in range(len(arr)):
        minElem = i
        for j in range(i+1,len(arr)):
            if arr[j] < arr[minElem]:
                minElem = j
        arr[i], arr[minElem] = arr[minElem], arr[i]
        
    return arr

